mapper.insert:
INSERT INTO BOARD
(TITLE, NAME, ID, 
CONTENT, INIP, INDATE, GROUPNO, GROUPORD, DEPTH)
VALUES(#{title}, #{name}, #{id}, #{content}, #{inip}, SYSDATE(), 
#{groupno}, #{groupord}, #{depth}
)

mapper.update:
UPDATE BOARD_REPLY SET REPLY_CONTENT = #{reply_content}
WHERE
REPLY_IDX = #{reply_idx}
